from abc import ABC, abstractmethod

class DiscountStrategy(ABC):
    @abstractmethod
    def apply(self, subtotal: float) -> float: ...

class NoDiscount(DiscountStrategy):
    def apply(self, subtotal: float) -> float:
        return subtotal

class PercentageDiscount(DiscountStrategy):
    def __init__(self, pct: float):
        self.pct = pct
    def apply(self, subtotal: float) -> float:
        return subtotal * (1 - self.pct)

class ThresholdDiscount(DiscountStrategy):
    def __init__(self, threshold: float, off: float):
        self.threshold = threshold
        self.off = off
    def apply(self, subtotal: float) -> float:
        return subtotal - self.off if subtotal >= self.threshold else subtotal

def main():
    strategy = PercentageDiscount(0.1)
    print(strategy.apply(100.0))

